mapper.select:
select a.*, b.groupCount from plat_Group a,
(select groupId, count(userId) groupCount from plat_GroupUser group by groupId) b
where a.id=b.groupid and a.createUserId=#{value, jdbcType=VARCHAR}

mapper.update:
update plat_Group
id=#{groupId,jdbcType=VARCHAR}, groupNum=#{groupNum,jdbcType=VARCHAR}, groupName=#{groupName,jdbcType=VARCHAR}, groupPwd=#{groupPwd,jdbcType=VARCHAR}, groupImg=#{groupImg,jdbcType=VARCHAR}, groupType=#{groupType,jdbcType=VARCHAR}, pId=#{pId,jdbcType=VARCHAR}, sort=#{sort,jdbcType=INTEGER}, createUserId=#{createUserId,jdbcType=VARCHAR}, adminUserIds=#{adminUserIds,jdbcType=VARCHAR}, descn=#{descn,jdbcType=VARCHAR}, cTime=#{CTime,jdbcType=TIMESTAMP},
where id=#{groupId,jdbcType=VARCHAR}

mapper.select:
select * from wt_MaPerson_Ref a, wt_Person b where a.personId=b.id and (
pName like '%${tag}%'
)
