mapper.update:
update wt_GroupInvite
aUserId=#{aUserId,jdbcType=VARCHAR}, bUserId=#{bUserId,jdbcType=VARCHAR}, groupId=#{groupId,jdbcType=VARCHAR}, inviteVector=#{inviteVector,jdbcType=INTEGER}, inviteMessage=#{inviteMessage,jdbcType=VARCHAR}, firstInviteTime=#{firstInviteTime,jdbcType=TIMESTAMP}, inviteTime=#{inviteTime,jdbcType=TIMESTAMP}, acceptFlag=#{acceptFlag,jdbcType=INTEGER}, managerFlag=#{managerFlag,jdbcType=INTEGER}, acceptTime=#{acceptTime,jdbcType=TIMESTAMP}, refuseMessage=#{refuseMessage,jdbcType=VARCHAR}, flag=#{flag,jdbcType=INTEGER},
where id=#{id,jdbcType=VARCHAR}

mapper.select:
select a.*, b.groupCount, c.inviteCount from plat_Group a, (select groupId, count(id) groupCount from plat_GroupUser group by groupId) b,
(select groupId, count(id) inviteCount from wt_GroupInvite where inviteVector<0 and flag=1 and acceptFlag=0 group by groupId) c
where a.id=b.groupId and a.id=c.groupId and a.adminUserIds=#{value, jdbcType=VARCHAR}
